["Initialization"] = "function (modTable)\n    ---@param unit string\n    ---@return number|nil\n    function modTable.parseGUID(unit)\n        local guid = UnitGUID(unit)\n        \n        if not guid then\n            return nil\n        end\n        \n        local id = select(6, strsplit(\"-\", guid))\n        \n        return id and tonumber(id) or nil\n    end\n    \n    modTable.glowKey = \"backfill-barrel\"\n    function modTable.startGlow(unitFrame)\n        Plater.StartPixelGlow(unitFrame.healthBar, modTable.config.glowColor, {\n                th = 4,\n                key = modTable.glowKey\n        }, modTable.glowKey)\n    end\n    function modTable.stopGlow(unitFrame)\n        Plater.StopPixelGlow(unitFrame.healthBar, modTable.glowKey)\n    end\n    \n    function modTable.customize(unitFrame, unitId)\n        if not unitId then\n            modTable.stopGlow(unitFrame)\n            return\n        end\n        \n        local npcID = unitFrame.namePlateNpcId or modTable.parseGUID(unitId)\n        \n        if not npcID then\n            modTable.stopGlow(unitFrame)\n            return\n        end\n        \n        if unitFrame.PlayerCannotAttack then\n            modTable.stopGlow(unitFrame)\n            return\n        end\n        \n        if npcID == modTable.npcID then\n            Plater.SetNameplateScale(unitFrame, modTable.config.scale)\n            Plater.SetNameplateColor (unitFrame, modTable.config.color)\n            \n            unitFrame.healthBar.unitName:SetText(modTable.config.name)\n            modTable.startGlow(unitFrame)\n        else\n            modTable.stopGlow(unitFrame)\n        end\n    end\n    \n    modTable.npcID = 223724 -- Backfill Barrel  \n    -- to test, uncomment this and go to the dummy\n    --modTable.npcID = 225984 -- Training Dummy (Dornogal)\nend",
