["Cast Update"] = "function (self, unitId, unitFrame, envTable, modTable)\n    envTable.UpdateCastbar()\nend",
["Constructor"] = "function (self, unitId, unitFrame, envTable, modTable)\n    local _, _, class = UnitClass(\"player\")\n    envTable.class = class\n    \n    envTable.UpdateCastbar = function()\n        local castBar = unitFrame.castBar\n        \n        local canInterrupt = castBar.canInterrupt\n        local castEndTime = castBar.spellEndTime\n        \n        local interruptReadyTime = 0\n        if envTable.kickId ~= nil then\n            local start = C_Spell.GetSpellCooldown(envTable.kickId).startTime\n            local duration = C_Spell.GetSpellCooldown(envTable.kickId).duration\n            interruptReadyTime = start + duration\n        else\n            interruptReadyTime = -1\n        end\n        \n        if not canInterrupt then\n            envTable.currentColor = \"protectedColor\"\n            Plater.SetCastBarColor(unitFrame, modTable.config[\"protectedColor\"])\n        elseif interruptReadyTime == 0 then\n            envTable.currentColor = \"readyColor\"\n            Plater.SetCastBarColor(unitFrame, modTable.config[\"readyColor\"])\n        elseif interruptReadyTime >= (castEndTime - 0.25) or interruptReadyTime == -1 then\n            envTable.currentColor = \"cooldownColor\"\n            Plater.SetCastBarColor(unitFrame, modTable.config[\"cooldownColor\"])\n        elseif interruptReadyTime < (castEndTime - 0.25) then\n            envTable.currentColor = \"almostColor\"\n            Plater.SetCastBarColor(unitFrame, modTable.config[\"almostColor\"])\n        end\n    end\nend",
["Initialization"] = "function (modTable)\n    modTable.kickIndex = {\n        [1] = {[1] = 6552, [2] = 6552, [3] = 6552}, -- Warrior\n        [2] = {[1] = 96231, [2] = 96231, [3] = 96231}, -- Paladin\n        [3] = {[1] = 147362, [2] = 147362, [3] = 187707}, -- Hunter\n        [4] = {[1] = 1766, [2] = 1766, [3] = 1766} ,-- Rogue\n        [5] = {[1] = nil, [2] = nil, [3] = 15487}, -- Priest\n        [6] = {[1] = 47528, [2] = 47528, [3] = 47528}, -- Death Knight\n        [7] = {[1] = 57994, [2] = 57994, [3] = 57994}, -- Shaman\n        [8] = {[1] = 2139, [2] = 2139, [3] = 2139}, -- Mage\n        [10] = {[1] = 116705, [2] = 116705, [3] = 116705}, -- Monk\n        [11] = {[1] = 78675, [2] = 106839, [3] = 106839, [4] = 106839}, -- Druid\n        [12] = {[1] = 183752, [2] = 183752}, -- Demon Hunter\n        [13] = {[1] = 351338, [2] = 351338, [3] = 351338} -- Evoker\n    }\nend",
["Cast Start"] = "function (self, unitId, unitFrame, envTable, modTable)\n    local spec = GetSpecialization()\n    envTable.spec = spec\n    if envTable.class ~= 9 then\n        envTable.kickId = modTable.kickIndex[envTable.class][envTable.spec]\n    else\n        -- Special warlock logic because warlocks are special\n        if envTable.spec == 1 or envTable.spec == 3 then -- Aff & Destro\n            if IsSpellKnown(19647, true) then -- Felhunter\n                envTable.kickId = 119910\n            else -- Look for Grimoire of Sacrifice\n                local i, found = 1, false\n                local buff = UnitBuff(\"player\", i)\n                while buff do\n                    if buff == \"Grimoire of Sacrifice\" then\n                        found = true\n                        buff = nil\n                    else\n                        i = i + 1\n                        buff = UnitBuff(\"player\", i)\n                    end\n                end\n                if found == true then\n                    envTable.kickId = 132409\n                else \n                    envTable.kickId = nil\n                end\n            end\n        elseif spec == 2 then -- Demo\n            if IsSpellKnown(89766, true) then -- Felguard\n                envTable.kickId = 119914\n            elseif IsSpellKnown(19647, true) then -- Felhunter\n                envTable.kickId = 119910\n            else \n                envTable.kickId = nil \n            end\n        end\n    end\n    \n    envTable.UpdateCastbar()\nend",
["Enter Combat"] = "function (self, unitId, unitFrame, envTable, modTable)\n    local spec = GetSpecialization()\n    envTable.spec = spec\nend",
["Cast Stop"] = "function (self, unitId, unitFrame, envTable, modTable)\n    if self.interrupted then\n        Plater.SetCastBarColor(unitFrame, modTable.config[\"interruptedColor\"])\n    else\n        Plater.SetCastBarColor(unitFrame, modTable.config[envTable.currentColor])\n    end\nend",
},
