["customText"] = "function()\n    -- Define a list of zoneIDs you're interested in\n    local zones = {1525, 1565,947, 1978,2024,2025,2023,2022,2026,1536,1533,1565,1525,1543,1570,1550,1970,905,619,630,650,625,680,634,646,790,885,830,882,62,14,875,875,876,862,863,864,895,942,896,1169,1355,1462 } -- Replace these with the zoneIDs you want to check\n    \n    -- Get all quests for the specified zones\n    local quests = {}\n    for _, zoneID in ipairs(zones) do\n        local zoneQuests = C_TaskQuest.GetQuestsForPlayerByMapID(zoneID)\n        if zoneQuests then -- Check if zoneQuests is not nil\n            for _, quest in ipairs(zoneQuests) do\n                quests[quest.questId] = quest\n            end\n        end\n    end\n    \n    local MyScanningTooltip = CreateFrame(\"GameTooltip\", \"MyScanningTooltip\", UIParent, \"GameTooltipTemplate\")\n    \n    \n    local matchingQuests = {}\n    for questID, quest in pairs(quests) do\n        if C_QuestLog.IsWorldQuest(questID) and tContains(aura_env.WQTrackerQuestList, questID) then\n            matchingQuests[#matchingQuests+1] = aura_env.QuestTitleFromID[questID]\n        end\n    end\n    \n    local matchingQuestsStr = \"\"\n    if #matchingQuests > 0 then\n        matchingQuestsStr = \"Tracked World Quests are currently up!\\n\"\n        for i, questTitle in ipairs(matchingQuests) do\n            matchingQuestsStr = matchingQuestsStr .. questTitle .. \"\\n\"\n        end\n    end\n    \n    return matchingQuestsStr\nend \n\n",
