["custom"] = "-- Define a list of zoneIDs you're interested in\nlocal zones = { 947, 1978,2024,2025,2023,2022,2026,1536,1533,1565,1525,1543,1570,1550,1970,905,619,630,650,625,680,634,646,790,885,830,882,62,14,875,875,876,862,863,864,895,942,896,1169,1355,1462 } -- Replace these with the zoneIDs you want to check\n\n-- Get all quests for the specified zones\nlocal quests = {}\nfor _, zoneID in ipairs(zones) do\n    local zoneQuests = C_TaskQuest.GetQuestsForPlayerByMapID(zoneID)\n    if zoneQuests then -- Check if zoneQuests is not nil\n        for _, quest in ipairs(zoneQuests) do\n            quests[quest.questId] = quest\n        end\n    end\nend\n\nlocal MyScanningTooltip = CreateFrame(\"GameTooltip\", \"MyScanningTooltip\", UIParent, \"GameTooltipTemplate\")\n\naura_env.QuestTitleFromID = setmetatable({}, { __index = function(t, id)\n            MyScanningTooltip:SetOwner(UIParent, \"ANCHOR_NONE\")\n            MyScanningTooltip:SetHyperlink(\"quest:\"..id)\n            local title = MyScanningTooltipTextLeft1:GetText()\n            MyScanningTooltip:Hide()\n            if title and title ~= RETRIEVING_DATA then\n                t[id] = title\n                return title\n            end\nend })\n\nlocal matchingQuests = {}\nfor questID, quest in pairs(quests) do\n    if C_QuestLog.IsWorldQuest(questID) and tContains(aura_env.WQTrackerQuestList, questID) then\n        print(\n            aura_env.QuestTitleFromID[questID])\n        matchingQuests[#matchingQuests+1] = \n        aura_env.QuestTitleFromID[questID]\n    end\nend\n\nreturn matchingQuests\n\n\n",
