["custom"] = "local s = aura_env.state\nif s and s.hide > 0 then\n    C_Timer.After(s.hide, function()\n            WeakAuras.ScanEvents(\"RELOE_SPELLCD_STATE_CLEAN\", s.GUID..s.spellID, s.expirationTime)\n    end)\nend\n\n\n\n\n",
