["parent"] = "20th Anniversary Event - Mount Mania",
["customText"] = "function()\n    -- ensure we have a valid state and unit\n    local state = aura_env.state\n    if not state then\n        return\n    end\n    local isOptionsOpen = WeakAuras.IsOptionsOpen()\n    local unit = state.unit\n    if not unit and isOptionsOpen then\n        unit = \"player\"\n    end\n    if not unit then\n        return\n    end\n    -- throttle how often the code runs\n    local now = GetTime()\n    if aura_env.now and now - aura_env.now < 0.5 then\n        return aura_env.text\n    end\n    aura_env.now = now\n    -- ensure we are in the mount mania area\n    if not aura_env.InMountManiaArena() then\n        aura_env.text = \"\"\n        return aura_env.text\n    end\n    -- extract which mount we need to get on from aura or widget\n    local aura\n    for i = 1, 40 do\n        aura = C_UnitAuras.GetAuraDataByIndex(unit, i, \"HELPFUL\")\n        if not aura then\n            break\n        elseif aura.sourceUnit and UnitIsUnit(aura.sourceUnit, unit) then\n            break\n        end\n        aura = nil\n    end\n    local spellName, spellID\n    -- during rounds the mount can be read from the aura\n    if aura then\n        spellName, spellID = aura.name, aura.spellId\n    end\n    -- between rounds we rely on the widget\n    if not spellID then\n        local widgetInfo = C_UIWidgetManager.GetSpellDisplayVisualizationInfo(6023)\n        if widgetInfo and widgetInfo.spellInfo and widgetInfo.spellInfo.shownState ~= 0 then\n            local temp = widgetInfo.spellInfo.spellID\n            if temp ~= 0 then\n                spellID = temp\n            end\n        end\n    end\n    -- last resort (requires to hover the icon on the nameplate)\n    if not spellID then\n        local tooltip = EmbeddedItemTooltip\n        if tooltip and tooltip:IsShown() then\n            spellName, spellID = tooltip:GetSpell()\n        end\n    end\n    -- ensure we have a spell and that it is a mount\n    if not spellID then\n        aura_env.text = \"\"\n        return aura_env.text\n    end\n    local mountID = C_MountJournal.GetMountFromSpell(spellID)\n    if not mountID and isOptionsOpen then\n        local ids = C_MountJournal.GetMountIDs()\n        mountID = ids[random(1, #ids)]\n    end\n    if not mountID then\n        aura_env.text = \"\"\n        return aura_env.text\n    end\n    -- update the text\n    local mountName, _, icon, isActive, isUsable, _, _, _, _, _, isCollected = C_MountJournal.GetMountInfoByID(mountID)\n    local _, _, sourceText = C_MountJournal.GetMountInfoExtraByID(mountID)\n    local texture = icon or 413588\n    local name = mountName or spellName or mountID\n    local status\n    if isActive then\n        status = format(\"|cff%s%s|r\", \"99FF99\", SPEC_ACTIVE)\n    elseif not isCollected then\n        status = format(\"|cff%s%s|r\", \"FF9999\", NOT_COLLECTED)\n    elseif not isUsable then\n        status = format(\"|cff%s%s|r\", \"FFFF99\", MOUNT_JOURNAL_FILTER_UNUSABLE)\n    end\n    sourceText = sourceText and tostring(sourceText) or \"\"\n    if status then\n        aura_env.text = format(\"|T%s:24:24|t %s (%s)\\n\\n%s\" or \"%s\\n\\n%s\", texture, name, status, sourceText)\n    else\n        aura_env.text = format(\"|T%s:24:24|t %s\\n\\n%s\", texture, name, sourceText)\n    end\n    -- if we have a status, it means we are either on it, or we can't mount it, otherwise we mount up\n    if status then\n        return aura_env.text\n    end\n    -- only mount if we're not in the options\n    if not isOptionsOpen then\n        C_MountJournal.SummonByID(mountID)\n        -- queue the /mountspecial\n        if aura_env.config.mountspecial and aura_env.mountID ~= mountID then\n            aura_env.mountID = mountID\n            C_Timer.After(5, function() DoEmote(\"mountspecial\") end)\n        end\n    end\n    return aura_env.text\nend",
