["custom"] = "_G.BDT_GlobalScripting = {};\n\nfunction IsWeeklyRewardForDelvesFull()\n    local worldActivitiesRewards = C_WeeklyRewards.GetActivities(Enum.WeeklyRewardChestThresholdType.World)\n    \n    local bestReward = worldActivitiesRewards[3]\n    \n    if bestReward then\n        return bestReward.level == 8\n    else\n        return false\n    end\nend\n\nfunction _G.BDT_GlobalScripting.GetShowValue(mode, ...)\n    local args = {...}\n    local allTrue = true\n    \n    if mode ~= aura_env.config.mode then\n        return false\n    end\n    \n    -- Iterate through all arguments\n    for _, value in ipairs(args) do\n        -- If any value is not true or is nil, set allTrue to false\n        if not value then\n            allTrue = false\n            break\n        end\n    end\n    \n    -- Return the 'and' condition of all conditions\n    -- If Autohide, we need to check the weekly rewards\n    if aura_env.config[\"autohide\"] then\n        local done = IsWeeklyRewardForDelvesFull()\n        return allTrue and not done\n    end\n    return allTrue\nend\n\n\n",
