["custom"] = "\naura_env.doTheeThing = function(allstates)\n    \n    CheckArmorKitScanner:SetOwner(WorldFrame, \"ANCHOR_NONE\");\n    CheckArmorKitScanner:SetInventoryItem(\"player\", 5);\n    \n    allstates[\"\"]=\n    {\n        changed = true,\n        show = true,\n        progressType = \"static\",\n        total = 120,\n        value = 0,\n        autoHide = false,\n    };\n    \n    local regions = {CheckArmorKitScanner:GetRegions()};\n    for i = 1,#regions do\n        local region = regions[i];\n        if(region and region:GetObjectType() == \"FontString\")then\n            local text = region:GetText();\n            if(text and string.match(text, \"Reinforced\"))then\n                local _,_, hours = string.find(text, \"(%d+) hour\");\n                local _,_, mins = string.find(text, \"(%d+) min\");\n                local _,_, seconds = string.find(text, \"(%d+) sec\");\n                if(hours)then\n                    allstates[\"\"].value = tonumber(hours) * 60;\n                elseif(mins)then\n                    allstates[\"\"].value = tonumber(mins);\n                elseif(seconds)then\n                    allstates[\"\"].value = tonumber(seconds) / 60;\n                end\n            end\n        end\n    end\nend\n\nif(not _G.CheckArmorKitScanner)then\n    CreateFrame(\"GameTooltip\", \"CheckArmorKitScanner\", nil, \"GameTooltipTemplate\");\nend\n\n\n",
