["customText"] = "function()\n    local s = aura_env.state\n    if not s then return end\n    \n    local c = aura_env.config\n    local idx, textFormat = unpack(aura_env.textFormat)\n    \n    \n    local percentRested = c.opt_showRested and aura_env.FormatRestedPercent(s.percentrested) or \"\"\n    local percentXP = idx <= 3 and format(\"%0.1f%%\", s.percentXP)..percentRested or \"\"\n    local currentXP = c.opt_abbrevNum and AbbreviateNumbers(s.currentXP) or s.currentXP\n    local totalXP = c.opt_abbrevNum and AbbreviateNumbers(s.totalXP) or s.totalXP\n    \n    return format(textFormat, percentXP, currentXP, idx ~= 4 and totalXP or \"\", idx > 3 and percentRested or \"\")\nend",
