["init"] = {
["custom"] = "local aura_env = aura_env\nlocal c = aura_env.config\naura_env.hooksSet = {false, false}\n\n\n-- hide xp bar\n\nlocal HideXpBar = function()\n    local f = StatusTrackingBarManager.MainStatusTrackingBarContainer\n    local xpBar = f.bars[4]\n    \n    if c.opt_hideDefault then\n        if xpBar and xpBar:IsShown() then\n            xpBar:Hide()\n            f.BarFrameTexture:Hide()\n        end\n    elseif xpBar and not xpBar:IsShown() then\n        xpBar:Show()\n        f.BarFrameTexture:Show()\n    end\nend\n\n\n\nfunction aura_env.SetHooks()\n    if not aura_env.hooksSet[1] then\n        if c.opt_mouseoverText then\n            local r = aura_env.region.bar\n            \n            r:HookScript(\"OnEnter\", function() \n                    WeakAuras.ScanEvents(\"XEII_XPBAR_SHOW\")\n            end)\n            \n            r:HookScript(\"OnLeave\", function() \n                    WeakAuras.ScanEvents(\"XEII_XPBAR_HIDE\")\n            end)\n            \n            aura_env.hooksSet[1] = true\n        end\n    end\n    \n    if not aura_env.hooksSet[2] then\n        hooksecurefunc(StatusTrackingBarManager, \"UpdateBarsShown\", HideXpBar)\n        HideXpBar()\n        aura_env.hooksSet[2] = true\n    end\nend\n\n\nlocal function GetTextFormat()\n    local idx = c.opt_textFormat\n    local textFormats = {\n        [1] = \"%s - %s / %s\",\n        [2] = \"%s - %s\",\n        [3] = \"%s\",\n        [4] = \"%s%s%s%s\",\n        [5] = \"%s%s / %s%s\",\n    }\n    \n    return {idx, textFormats[idx]}\nend\naura_env.textFormat = GetTextFormat()\n\n--text colour logic\nlocal function GetHexColour(colour)\n    if colour then\n        local r, g, b = unpack(colour)\n        r = r <= 1 and r >= 0 and r or 0\n        g = g <= 1 and g >= 0 and g or 0\n        b = b <= 1 and b >= 0 and b or 0\n        return string.format(\"FF%02x%02x%02x\", r*255, g*255, b*255)\n    end\nend\nlocal percentColour = \"|c\"..GetHexColour(c.opt_restedColour)\n\n\nfunction aura_env.FormatRestedPercent(restedPercent)\n    local formattedPercent = \"\"\n    \n    if restedPercent < 1 then\n        if restedPercent < 0.01 and restedPercent > 0 then\n            formattedPercent = string.format(\" +%.2f%%\", restedPercent+0.01)\n        end\n    elseif restedPercent < 10 then\n        formattedPercent = string.format(\" +%.1f%%\", restedPercent)\n    else\n        formattedPercent = string.format(\" +%.0f%%\", restedPercent)\n    end\n    \n    return percentColour..formattedPercent..\"|r\"\nend",
