["custom"] = "-- OnInit code\nif not aura_env.frame then\n    -- Create a frame that overlays the region (your texture)\n    aura_env.frame = CreateFrame(\"Button\", nil, aura_env.region)\n    aura_env.frame:SetAllPoints(aura_env.region)\n    aura_env.frame:SetFrameLevel(aura_env.region:GetFrameLevel() + 1)\n    aura_env.frame:RegisterForClicks(\"AnyUp\")\n    \n    -- Mouseover script to trigger the highlight event\n    aura_env.frame:SetScript(\"OnEnter\", function(self)\n            WeakAuras.ScanEvents(\"BDT_HIGHLIGHT_BUTTON\", true)\n    end)\n    \n    -- Optional: Script for when the mouse leaves the frame\n    aura_env.frame:SetScript(\"OnLeave\", function(self)\n            WeakAuras.ScanEvents(\"BDT_HIGHLIGHT_BUTTON\", false)\n    end)\n    \n    -- OnClick script to set the global variable\n    aura_env.frame:SetScript(\"OnClick\", function(self, button)\n            _G.isMinimized = not _G.isMinimized\n            WeakAuras.ScanEvents(\"BDT_CLICK_BUTTON\")\n    end)\nend\n\nWeakAuras.ScanEvents(\"BDT_INIT_BUTTON\")",
