["events"] = "PLAYER_TALENT_UPDATE, TRAIT_CONFIG_UPDATED, TRAIT_CONFIG_REFRESH, ACTIVE_PLAYER_SPECIALIZATION_CHANGED, TRAIT_CONFIG_DELETED, TRAIT_TREE_CHANGED, READY_CHECK",
