["custom"] = "function(allstates, event, configId)\n    if event ~= \"TRAIT_CONFIG_REFRESH\" then\n        aura_env.Scan()\n        return false\n    end\n    \n    local defaultConfig = false\n    local currentSpecID = PlayerUtil.GetCurrentSpecID()\n    \n    if currentSpecID == nil then return false end\n    \n    local lastSelectedSavedConfigID = C_ClassTalents.GetLastSelectedSavedConfigID(currentSpecID)\n    if lastSelectedSavedConfigID == nil then\n        lastSelectedSavedConfigID = C_ClassTalents.GetActiveConfigID()\n        defaultConfig = true\n    end\n    \n    local configInfo = C_Traits.GetConfigInfo(lastSelectedSavedConfigID)\n    \n    if configInfo == nil then\n        if aura_env.retries < 5 then\n            aura_env.Retry()\n            return false\n        end\n    end\n    \n    local configName = configInfo and configInfo.name or \"\"\n    \n    if defaultConfig then\n        configName = configName .. \" (Default)\"\n    end\n    \n    if C_ClassTalents.GetStarterBuildActive() then\n        configName = \"Starter Build\"\n    end\n    \n    if aura_env.config.colorLoadout then\n        local textColor = CreateColor(unpack(aura_env.config.textColor))\n        configName = textColor:WrapTextInColorCode(configName)\n    end\n    \n    allstates[1] = {\n        show = true,\n        changed = true,\n        name = configName\n    }\n    aura_env.retries = 0\n    return true\nend",
