["init"] = {
["custom"] = "local DelvesBountifulList = aura_env\n\n_G.DelvesBountifulList = DelvesBountifulList\n\n-- Organized list of zones, their map IDs, and corresponding delves\nDelvesBountifulList.Zones = {\n    -- Isle of Dorn (Map ID: 2248)\n    [\"Isle of Dorn\"] = {\n        uiMapID = 2248,\n        delves = {\n            { id = 7787, name = \"Earthcrawl Mines\" },\n            { id = 7781, name = \"Kriegval's Rest\" },\n            { id = 7779, name = \"Fungal Folly\" },\n        }\n    },\n    \n    -- Hallowfall (Map ID: 2215)\n    [\"Hallowfall\"] = {\n        uiMapID = 2215,\n        delves = {\n            { id = 7789, name = \"Skittering Breach\" },\n            { id = 7785, name = \"Nightfall Sanctum\" },\n            { id = 7783, name = \"The Sinkhole\" },\n            { id = 7780, name = \"Mycomancer Cavern\" },\n        }\n    },\n    \n    -- The Ringing Deeps (Map ID: 2214)\n    [\"The Ringing Deeps\"] = {\n        uiMapID = 2214,\n        delves = {\n            { id = 7782, name = \"The Waterworks\" },\n            { id = 7788, name = \"The Dread Pit\" },\n            { id = 5143, name = \"Excavation Site 9\" },\n        }\n    },\n    \n    -- Azj-Kahet (Map ID: 2255)\n    [\"Azj-Kahet\"] = {\n        uiMapID = 2255,\n        delves = {\n            { id = 7790, name = \"The Spiral Weave\" },\n            { id = 7784, name = \"Tak-Rethan Abyss\" },\n            { id = 7786, name = \"The Underkeep\" },\n        }\n    },\n    [\"Undermine\"] = {\n        uiMapID = 2346,\n        delves = {\n            { id = 8246, name = \"Sidestree Sluice\" },\n        }\n    }\n}\n\n-- Returns uiMapID. Uses the Enum: Enum.UIMapType to find the correct map. 3 means \"Zone\", so we're trying to get he correct parent. Recursive.\n-- This is necessary because some maps are subzones of other maps (Like Dornogal being a subzone of Isle of Dorn), and we sometimes need to get the parent map ID to find the correct zone.\nfunction DelvesBountifulList.GetMapAtZoneLevel3(uiMapID)\n    local mapInfo = C_Map.GetMapInfo(uiMapID)\n    if mapInfo.mapType > 3 and mapInfo.parentMapID > 0 then\n        return DelvesBountifulList.GetMapAtZoneLevel3(mapInfo.parentMapID)\n    else\n        return uiMapID\n    end\nend\n\n-- Function to return the list of bountiful delves for a specific map ID\nfunction DelvesBountifulList.GetBountifulDelvesForMapID(uiMapID)\n    local bountifulDelves = {}\n    \n    -- Check each zone and see if the map ID matches\n    for zoneName, zoneData in pairs(DelvesBountifulList.Zones) do\n        if zoneData.uiMapID == uiMapID then\n            -- Add all delves for this map to the list\n            for _, delve in ipairs(zoneData.delves) do\n                local areaPoiInfo = C_AreaPoiInfo.GetAreaPOIInfo(uiMapID, delve.id)\n                if areaPoiInfo then\n                    table.insert(bountifulDelves, areaPoiInfo)\n                end\n            end\n            break  -- Stop once we've found the correct map\n        end\n    end\n    \n    return bountifulDelves\nend\n\n-- Function to return the list of bountiful delves for all TWW maps\nfunction DelvesBountifulList.GetBountifulDelves()\n    local bountifulDelves = {}\n    \n    -- Track delves already added to avoid duplicates across multiple maps\n    local addedDelves = {}\n    \n    -- Iterate through all zones in the DelvesBountifulList.Zones table\n    for zoneName, zoneData in pairs(DelvesBountifulList.Zones) do\n        -- Get delves for this map (zone)\n        for _, delve in ipairs(zoneData.delves) do\n            if not addedDelves[delve.id] then\n                local areaPoiInfo = C_AreaPoiInfo.GetAreaPOIInfo(zoneData.uiMapID, delve.id)\n                if areaPoiInfo then\n                    table.insert(bountifulDelves, areaPoiInfo)\n                    addedDelves[delve.id] = true -- Mark this delve ID as added\n                end\n            end\n        end\n    end\n    \n    return bountifulDelves\nend\n\n-- Function to return the list of bountiful delves for the current map\nfunction DelvesBountifulList.GetBountifulDelvesForCurrentMap()\n    local uiMapID = C_Map.GetBestMapForUnit(\"player\")\n    local correctMapID = DelvesBountifulList.GetMapAtZoneLevel3(uiMapID)\n    return DelvesBountifulList.GetBountifulDelvesForMapID(correctMapID)\nend\n\n-- Function to check if a zone has any delves to display\nfunction DelvesBountifulList.ZoneHasDelves(zoneDelves, allDelvesList)\n    for _, delve in ipairs(zoneDelves) do\n        for _, activeDelve in ipairs(allDelvesList) do\n            if delve.id == activeDelve.areaPoiID then\n                return true\n            end\n        end\n    end\n    return false\nend\n\n-- Function to get the timer of any Bountiful delves that are active\nfunction DelvesBountifulList.GetActiveDelveTimer()\n    local nextReset = GetQuestResetTime()\n    \n    return nextReset\nend\n\n-- Function to layout text that will be displayed in the aura\nfunction DelvesBountifulList.LayoutText()\n    local allDelves = DelvesBountifulList.GetBountifulDelves()\n    \n    local text = \"\"\n    \n    -- Show delves for zones only if they have Bountiful Delves\n    for zoneName, zoneData in pairs(DelvesBountifulList.Zones) do\n        local zoneInfo = C_Map.GetMapInfo(zoneData.uiMapID)\n        if DelvesBountifulList.ZoneHasDelves(zoneData.delves, allDelves) then\n            \n            if text ~= \"\" then\n                text = text .. \"\\n\"\n            end\n            \n            text = text .. \"|cffffd700\" .. zoneInfo.name .. \"|r\\n\"  -- Zone header with color\n            \n            for i, delve in ipairs(zoneData.delves) do\n                for _, otherDelve in ipairs(allDelves) do\n                    if delve.id == otherDelve.areaPoiID then\n                        text = text .. otherDelve.name .. \"\\n\"  -- Add icon before delve name\n                    end\n                end\n            end\n        end\n    end\n    \n    return text\nend\n\n",
["do_custom"] = true,
},
