["init"] = {
["custom"] = "WeakAuras.WatchGCD()\n\n---- Spell IDs ------------------------------------------------------------------------------------------------\naura_env.ids = {\n    -- Abilities\n    Backstab = 53,\n    BlackPowder = 319175,\n    EchoingReprimand = 385616,\n    Eviscerate = 196819,\n    Flagellation = 384631,\n    Rupture = 1943,\n    SecretTechnique = 280719,\n    Sepsis = 385408,\n    ShadowBlades = 121471,\n    ShadowDance = 185313,\n    Shadowstrike = 185438,\n    ShurikenStorm = 197835,\n    ShurikenTornado = 277925,\n    SliceAndDice = 315496,\n    SymbolsOfDeath = 212283,\n    ThistleTea = 381623,\n    Vanish = 1856,\n    \n    -- Talents\n    InvigoratingShadowdust = 382523,\n    \n    -- Buffs/Debuffs\n    ShadowDanceBuff = 185422,\n    FlagellationMasteryBuff = 394758,\n    Premeditation = 343160,\n    DanseMacabre = 393969,\n}\n\n---- Utility Functions ----------------------------------------------------------------------------------------\naura_env.OutOfRange = false\n\naura_env.NGSend = function(Name, Extra)\n    WeakAuras.ScanEvents(\"NG_GLOW_EXCLUSIVE\", Name, Extra)\n    WeakAuras.ScanEvents(\"NG_OUT_OF_RANGE\", aura_env.OutOfRange)\nend\n\nlocal function GetNumSpells()\n    local _, _, Offset, NumSpells = GetSpellTabInfo(GetNumSpellTabs())\n    return Offset + NumSpells\nend\n\naura_env.OffCooldown = function(spellID)\n    if spellID == nil then\n        local c = a < b -- Throw an error\n    end\n    \n    if not IsPlayerSpell(spellID) then return false end\n    if aura_env.config[tostring(spellID)] == false then return false end\n    \n    local usable, nomana = IsUsableSpell(spellID)\n    if (not usable) and (not nomana) then return false end\n    \n    local _, Duration = GetSpellCooldown(spellID)        \n    local OffCooldown = Duration == nil or Duration == 0 or Duration == WeakAuras.gcdDuration()\n    if not OffCooldown then return false end\n    \n    \n    local function FindSpellIdx(SpellName)\n        local _, _, Offset, NumSpells = GetSpellTabInfo(GetNumSpellTabs())\n        for i = 1, Offset + NumSpells do\n            local spell = GetSpellBookItemName(i, BOOKTYPE_SPELL)\n            if spell == SpellName then return i end\n        end\n        return nil\n    end\n    \n    local SpellIdx = FindSpellIdx(GetSpellInfo(spellID))\n    local InRange = SpellIdx and IsSpellInRange(SpellIdx, BOOKTYPE_SPELL, \"target\") -- safety\n    if InRange == 0 then\n        aura_env.OutOfRange = true\n        return false\n    end\n    \n    return true\nend\n\naura_env.IsCasting = function(spellID)\n    return select(9, UnitCastingInfo(\"player\")) == spellID\nend\n\naura_env.OffCooldownNotCasting = function(spellID)\n    return aura_env.OffCooldown(spellID) and not aura_env.IsCasting(spellID)\nend\n\naura_env.GetStacks = function(unit, spellID, filter)\n    local _,_,Stacks = WA_GetUnitAura(unit, spellID, filter)\n    if Stacks == nil then Stacks = 0 end\n    return Stacks\nend\n\naura_env.GetPlayerStacks = function(spellID)\n    return aura_env.GetStacks(\"player\", spellID)\nend\n\naura_env.GetTargetStacks = function(spellID)\n    return aura_env.GetStacks(\"target\", spellID, \"PLAYER|HARMFUL\")\nend\n\naura_env.GetRemainingAuraDuration = function(unit, spellID, filter)\n    if spellID == nil then\n        local c = a < b -- Throw an error\n    end\n    \n    if filter == nil then filter = \"PLAYER\" end\n    local AuraFound = WA_GetUnitAura(unit, spellID, filter)\n    \n    if AuraFound == nil then return 0 end\n    local Expiration = select(6, WA_GetUnitAura(unit, spellID, filter))\n    if Expiration == nil then return 0 end\n    return Expiration - GetTime()\nend\n\naura_env.GetRemainingDebuffDuration = function(unit, spellID)\n    return aura_env.GetRemainingAuraDuration(unit, spellID, \"HARMFUL|PLAYER\")\nend\n\naura_env.GetRemainingSpellCooldown = function(spellID)\n    if spellID == nil then\n        local c = a < b -- Throw an error\n    end\n    \n    local usable, nomana = IsUsableSpell(spellID)\n    if (not usable) and (not nomana) then return false end\n    \n    local Start, Duration = GetSpellCooldown(spellID)\n    local Remaining = Start + Duration - GetTime()\n    if (Duration == nil or Duration == WeakAuras.gcdDuration()) then Remaining = 0 end\n    return Remaining\nend",
