["custom"] = "function()\n    local a = aura_env\n    if not a.lastRefresh or a.lastRefresh < GetTime() - a.refreshRate then\n        \n        if WA_GetUnitBuff(\"player\", 51690) then return false end\n        local Result;\n        \n        local EventState = false;\n        \n       -- local Cleave = 5;\n       -- if IsPlayerSpell(196924) then Cleave = 8 end\n        \n        a.lastRefresh = GetTime()\n        if a.config.EarlyRolls == true then a.RollDurations(1.1) end\n        if aura_env.config.BladeFlurry == false or not InCombatLockdown() then return false end\n        \n        local counter = 0\n        local name = select(1,GetSpellInfo(1766))\n        \n        if a.config.ydRange10 == true then name = select(1,GetSpellInfo(921)) end\n        \n        for i = 1, 40 do\n            if UnitIsDead(\"player\") or UnitExists(\"nameplate\"..i) == false then \n                break\n            end\n            local unit = \"nameplate\"..i\n            --if UnitExists(unit) and not UnitIsFriend(\"player\", unit) and  WeakAuras.CheckRange(unit, Cleave, \"<=\") and UnitCanAttack(\"player\", unit) and (UnitReaction(unit, \"player\") < 4 or a.config.IgnoreNeutrals == false or UnitGUID(\"target\") == UnitGUID(unit)) then\n            if UnitExists(unit) and not UnitIsFriend(\"player\", unit) and  IsSpellInRange(name, unit) == 1 and UnitCanAttack(\"player\", unit) and (UnitReaction(unit, \"player\") < 4 or a.config.IgnoreNeutrals == false or UnitGUID(\"target\") == UnitGUID(unit)) then\n                Result = a.Blaklisted(unit)\n                EventState = Result == \"Blocked\";\n                if Result == \"Blocked\" then \n                    a.WarnPotential = false  \n                    counter = -50 \n                    break \n                elseif Result ~= \"Prevent\" then\n                    counter = counter + 1\n                    if(counter > 39) then \n                        break \n                    end \n                end\n            end\n            \n        end\n        \n        WeakAuras.ScanEvents(\"WA_OUTLAW_DISABLE_BLADEFLURRY\", EventState)\n        WeakAuras.ScanEvents(\"WA_OUTLAW_BF_USE_ON_CD\", counter)\n        \n        a.result = counter\n        a.UseBladeFlurry = a.result ~= nil and (a.result > 1 or a.BladeFlurrySingle(a.result > -1)) and aura_env.config.BladeFlurry == true\n    end\n    \n    a.AoEScenario = a.result ~= nil and a.result > 1 and aura_env.config.BladeFlurry == true;\n    return InCombatLockdown() and not UnitIsDead(\"player\") and a.UseBladeFlurry and aura_env.InStealthWindow(false, aura_env.config.BFStealth) == false\nend",
