["custom"] = "function()\n    \n    local a = aura_env\n    local c = a.config\n    if c.Kir == false or IsPlayerSpell(381989) == false then return false end\n    local KirCount = 0\n    local TotalAuras = 0\n    \n    if c.KirMode ~= 2 then\n        local KirReq = 2\n        --[[for i = 1, #a.KirAuras do \n            if WA_GetUnitBuff(\"player\", a.KirAuras[i]) then \n                KirCount = KirCount + 1\n            end   \n        end]]--\n        if select(1,WeakAuras.GetNumSetItemsEquipped(1566)) >= 4 then \n            KirReq = 3\n        end\n        for i = 1, #a.Rolls do \n            if WA_GetUnitBuff(\"player\", a.Rolls[i]) then \n                TotalAuras = TotalAuras + 1\n                --if tContains(a.KirAuras, a.Rolls[i]) or \n                -- (a.KirBuildExtendGM() == true and a.Rolls[i] == 193358) then \n                KirCount = KirCount + 1\n                -- end    \n            end\n        end\n        -- if c.KirMode == 2 then KirReq = 1 end\n        return KirCount > KirReq and aura_env.InStealthWindow(false, aura_env.config.KIRStealth) == false--and (not WA_GetUnitBuff(\"player\", 185422) or TotalAuras >= 5)\n    else\n        for i = 1, #a.Rolls do \n            if WA_GetUnitBuff(\"player\", a.Rolls[i]) then \n                KirCount = KirCount + a.BuffVal[i]\n            end \n        end\n        return KirCount >= a.KirReq and aura_env.InStealthWindow(false, aura_env.config.KIRStealth) == false\n    end\nend",
