["custom"] = "function(event,arg1,_,_,unit,...) --Calculates roll grade and displays it. Also displays buffs extended by kir when kir is used.\n    if event == \"RTB_DISPLAY_REFRESH\" and arg1 ~= nil then return aura_env.config.ResultMode < 4 end\n    if event == \"RTB_DISPLAY_REFRESH\" and arg1 == nil then return end\n    if unit ~= UnitGUID(\"player\") or (select(8,...) ~= 315508 and select(8,...) ~= 381989) then return end\n    local a = aura_env\n    local b = aura_env.config\n    a.Explanation = \" \"\n    local rankSolo = \"\";\n    --local Matches = 0;\n    \n    local HasT314P = select(1,WeakAuras.GetNumSetItemsEquipped(1566)) >= 4\n    \n    if b.RTB == false or a.FailState == true or #a.BuffVal == 0  then return false end\n    \n    -- if unit == UnitGUID(\"player\") then\n    if select(8,...) == 315508 then --Roll the Bones is used\n        if a.ItsTimer ~= nil then \n            a.ItsTimer:Cancel();\n        end\n        local Apply = GetTime()\n        local Score = 0;\n        --a.BuffApplied = {}\n        -- if #a.BuffApplied > 0 then\n        a.BuffApplied = table.wipe(a.BuffApplied) \n        -- end\n        a.Output = \"\"\n        local Matches = 0;\n        \n        local AttemptCount = 1\n        \n        --Retries to get buffs up to 10 times with more lenient time frames (If you run too lenient time frames, you run the risk of allowing CTO procs in)\n        while(Score == 0 and AttemptCount <= 10) do\n            Score, Matches, rankSolo = aura_env.GetRolls(Score, Matches, 0.05 + AttemptCount / 100 * 1.7, 0.1 + AttemptCount / 100 * 1.5, Apply) \n            AttemptCount = AttemptCount + 1\n        end\n        \n        if Matches > 0 and ((tContains(a.AllowedRollCount, Matches) == false and HasT314P == false) \n            or (tContains(a.AllowedRollCount4p, Matches) == false and HasT314P == true)) then\n            print(\"|cFF00FF00\"..a.id..\"|r - |cFFFF0000Warning!|rRTB returned unexpected amount of buffs!\") --Warning user soemthing is not right\n            local lowestIndex = 0;\n            local lowestValue = false;\n            for k, v in ipairs(a.BuffApplied) do\n                local ApplicationTime = select(6, WA_GetUnitBuff(\"player\", v)) - select(5, WA_GetUnitBuff(\"player\", v))\n                if not lowestValue or ApplicationTime < lowestValue then\n                    lowestIndex = k;\n                    lowestValue = ApplicationTime;\n                end\n            end\n            tremove(a.BuffApplied, lowestIndex)\n            \n            Score = 0;\n            a.Explanation = \"\";\n            a.Output = \"\";\n            Matches = 0;\n            \n            for i = 1, 6 do\n                \n                local icon = select(3,GetSpellInfo(a.Rolls[i]))\n                \n                if tContains(a.BuffApplied, a.Rolls[i])then\n                    if a.AoEScenario == true and a.Rolls[i] == 193358 and a.AoEBonus > 0 then\n                        Score = Score + a.AoEBonus\n                        rankSolo = b.SoloPrint\n                    end\n                    \n                    Matches = Matches + 1      \n                    \n                    Score = Score + a.BuffVal[i]\n                    a.Explanation = a.Explanation..a.buffExp[i]\n                    a.Output = a.Output..\"|T\"..icon..a.IconSize;\n                end\n            end\n        end\n        \n        --if no rolls were gotten, retry with small delay\n        \n        a.KirBuffs() --prevents aura from prompting reroll in cases where kir buffs would be lost\n        \n        --if b.Sleight == true and Matches == 1 and Score >= a.Reqs[5] then Score = a.Reqs[6] rankSolo = \"~\" end \n        \n        if Score > 0 and a.Output ~= \"\" then \n            local Decision = \"\"\n            for z = 1, #a.Reqs do\n                if Score >= a.Reqs[z] then \n                    if b.ResultMode == 1 or b.ResultMode == 3 then\n                        Decision = \"|\"..a.Ranks[z]..rankSolo..\"|r\"\n                    end\n                    if z == 1 and b.Audio == true then\n                        a.SoundHandler(b[\"RollAudio\"][\"Chant\"], b.Audio, a.AudioChannels[b.Channel], false)\n                    end\n                    if b.TTSGrades == true then \n                        C_VoiceChat.SpeakText(aura_env.config.VoiceID, a.RanksTTS[z], Enum.VoiceTtsDestination.LocalPlayback, b.TTSSpeed, b.TTSVolume)\n                    else    \n                        if b.Audio == true then\n                            a.SoundHandler(a.Audio[z], b.Audio, a.AudioChannels[b.Channel], false)\n                        end\n                    end\n                    if b.Events == true then\n                        WeakAuras.ScanEvents(\"WA_MUROPAKETTI_RTB_RESULTS\", a.BuffApplied, Score, Decision, GetTime()) --could be used to show gained buffs via different aura if the one used here is to not your liking. Payloads (buffs gained, buff score, grade, time)\n                    end\n                    \n                    break;\n                end\n                --z = z + 1\n            end\n            \n            if b.ResultMode < 3 then\n                a.Output = a.Output..\"\\n\"..Decision\n            else \n                a.Output = Decision\n            end\n            \n            return b.ResultMode < 4\n        else    \n            if b.Error == true then\n                if a.Output == \"\" then\n                    print(\"|cFFff0000Error|r a.Output is empty.\")\n                elseif Score == 0 then\n                    print(\"|cFFff0000Error|r Roll value returned 0. Check your buff weights.\")\n                else \n                    print(\"|cFFff0000Error|r Unknown error.\")\n                end\n            end\n            --Ye, something really went wrong. Lag between server and the client? IDK really.\n            print(a.id..\" - |cFFff0000Error|r Failed to return any rolls. This should not happen.\")--\\nSwapping to less accurate check.\")\n            print(\"Retrying with small delay\")\n            \n            Score = 0 \n            a.Output = \"\"\n            \n            C_Timer.After(0.05, function()\n                    AttemptCount = 1\n                    \n                    while(Score == 0 and AttemptCount <= 10) do\n                        Score, Matches, rankSolo = aura_env.GetRolls(Score, Matches, 0.05 + AttemptCount / 100 * 1.7, 0.1 + AttemptCount / 100 * 1.5, Apply) \n                        AttemptCount = AttemptCount + 1\n                    end \n                    if Score > 0 and a.Output ~= \"\" then \n                        local Decision = \"\"\n                        for z = 1, #a.Reqs do\n                            if Score >= a.Reqs[z] then \n                                if b.ResultMode == 1 or b.ResultMode == 3 then\n                                    Decision = \"|\"..a.Ranks[z]..rankSolo..\"|r\"\n                                end\n                                if z == 1 and b.Audio == true then\n                                    a.SoundHandler(b[\"RollAudio\"][\"Chant\"], b.Audio, a.AudioChannels[b.Channel], false)\n                                end\n                                if b.TTSGrades == true then \n                                    C_VoiceChat.SpeakText(aura_env.config.VoiceID, a.RanksTTS[z], Enum.VoiceTtsDestination.LocalPlayback, b.TTSSpeed, b.TTSVolume)\n                                else    \n                                    if b.Audio == true then\n                                        a.SoundHandler(a.Audio[z], b.Audio, a.AudioChannels[b.Channel], false)\n                                    end\n                                end\n                                break;\n                            end\n                            --z = z + 1\n                        end\n                        \n                        if b.ResultMode < 3 then\n                            a.Output = a.Output..\"\\n\"..Decision\n                        else \n                            a.Output = Decision\n                        end\n                        \n                        WeakAuras.ScanEvents(\"RTB_DISPLAY_REFRESH\", true)\n                        \n                        return b.ResultMode < 4\n                    else\n                        return false;\n                    end\n                    \n            end)\n        end\n        \n        \n        \n        --Needs to be reworked. Will break if anything like creation spark ever exists again. Maybe C_Timers into proper buff trackers instead?\n    elseif select(8,...) == 381989 then --Keep it Rolling is used\n        a.Output = \"\"\n        local Buffs = 0;\n        \n        if b.ResultMode < 3 then\n            for i = 1, 6 do\n                local BuffName = select(1,GetSpellInfo(a.Rolls[i]))\n                if WA_GetUnitBuff(\"player\", BuffName) then\n                    Buffs = Buffs + 1\n                    \n                    if not tContains(a.KirredBuffs, a.Rolls[i]) then    \n                        tinsert(a.KirredBuffs, a.Rolls[i]) \n                    end\n                    \n                    a.Output = a.Output..\"|T\"..select(3,GetSpellInfo(a.Rolls[i]))..a.IconSize;\n                end\n            end\n        end\n        \n        if b.KirBuffs == true and Buffs < 5 then--and (not WA_GetUnitBuff(\"player\", 199603) or not WA_GetUnitBuff(\"player\", 193356) or not WA_GetUnitBuff(\"player\", 193359)) then--and Buffs < 4 then\n            \n            local KirHighestDur = 0\n            for z = 1, 6 do\n                if WA_GetUnitBuff(\"player\", a.Rolls[z]) then\n                    local Duration = select(6,WA_GetUnitBuff(\"player\", a.Rolls[z])) - GetTime()               \n                    if Duration > KirHighestDur then\n                        KirHighestDur = Duration\n                    end\n                end\n            end\n            \n            --code which would break with creation spark #1\n            if KirHighestDur > 39 then  \n                KirHighestDur = KirHighestDur - 39\n                a.ItsTimer = C_Timer.NewTimer(KirHighestDur, function()\n                        --a.BuffApplied = {} \n                        a.BuffApplied = table.wipe(a.BuffApplied)\n                end) \n            else\n                --a.BuffApplied = {}\n                a.BuffApplied = table.wipe(a.BuffApplied)\n            end\n            --end of offending code\n            \n            --Monitor for errors\n        elseif b.KirBuffs == true then  --if 5 or more buffs were extended\n            local GoodKirBuffs = {}--select(6,WA_GetUnitBuff(\"player\", 199603))  - GetTime(), select(6,WA_GetUnitBuff(\"player\", 193356))  - GetTime(), select(6,WA_GetUnitBuff(\"player\", 193359))  - GetTime()}\n            \n            for i = 1, #a.Rolls do\n                if WA_GetUnitBuff(\"player\", a.Rolls[i]) ~= nil then\n                    tinsert(GoodKirBuffs, select(6,WA_GetUnitBuff(\"player\", a.Rolls[i])) - GetTime())\n                end\n            end          \n            \n            --taking the lowest duration of the extended buffs\n            local minDur = math.huge\n            for AnotherLoop = 1, #GoodKirBuffs  do\n                minDur = minDur < GoodKirBuffs[AnotherLoop] and minDur or GoodKirBuffs[AnotherLoop]\n            end\n            \n            --code which would break with creation spark #2\n            --emptying out the roll table as soon as the buff with lowest duration has expired\n            a.ItsTimer = C_Timer.NewTimer(minDur, function()\n                    --a.BuffApplied = {}\n                    a.BuffApplied = table.wipe(a.BuffApplied)\n            end) \n            --end of offending code\n        end\n        \n        if Buffs > 0 then\n            a.SoundHandler(128883, b.Audio, a.AudioChannels[b.Channel], false)\n            if b.ResultMode == 1 then\n                a.Output = a.Output..\"\\nExtended\" \n            end\n            return b.ResultMode < 3\n        end\n    end\n    -- end\nend",
