["custom"] = "function(allstates,event, timestamp, subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags, spellId, spellName, ...)\n    local playerGUID = UnitGUID(\"player\")\n    local targetGUID = UnitGUID(\"target\")\n    local swingTime = UnitAttackSpeed(\"player\")\n    local state = allstates[\"\"]\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\"  then\n        if sourceGUID == playerGUID  then\n            if not state then state = {} end\n            if subEvent == \"SPELL_ENERGIZE\" and spellId == 406834  then --Crusading Strikes\n                aura_env.isEnergized = true\n            elseif (subEvent == \"SPELL_DAMAGE\" or subEvent == \"SPELL_MISSED\") and spellId == 408385 and destGUID == targetGUID  then --Crusader Strike\n                if aura_env.isEnergized == true then\n                    aura_env.isEnergized = false\n                    state.expirationTime = swingTime * 2 + GetTime()\n                    state.paused = false\n                    state.changed = true\n                    aura_env.timer = C_Timer.NewTimer(swingTime, function() WeakAuras.ScanEvents(\"WA_PAUSE_SWING_TIMER\") end)\n                else\n                    \n                    if aura_env.timer and not aura_env.timer:IsCancelled() then\n                        aura_env.timer:Cancel()\n                    end\n                    state.paused = false\n                    state.expirationTime = swingTime + GetTime()\n                    state.changed = true\n                end\n            end\n        end\n    elseif event == \"STATUS\" then\n        if not state then state = {} end\n        state.show = true\n        state.changed = true\n        state.progressType = \"timed\"\n        state.autoHide = false\n        state.duration = swingTime*2\n        state.expirationTime = swingTime*2+GetTime()\n        state.paused = true\n        state.remaining = swingTime*2\n    elseif event == \"WA_PAUSE